import React, { useState } from "react";
import { NavLink } from "react-router-dom";
import { Button, Modal, Table } from 'react-bootstrap';
import { HamburgetMenuClose, HamburgetMenuOpen } from "../Icons";
import ManagementGroups from "./ManagementGroups"; // Make sure to import the ManagementGroups component

const ManageGroupsComponent = () => {
  const [teacherGroups, setTeacherGroups] = useState([
    { id: 1, name: 'FT-SD-A3', numberOfStudents: 25 },
    // Add more default groups as needed
  ]);

  const [show, setShow] = useState(false);
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const [data, setData] = useState({
    username: "",
  });

  const [whenFieldsNotInput, setWhenFieldsNotInput] = useState("");

  const handleDelete = (groupId) => {
    // Implement delete logic and update state accordingly
    const updatedGroups = teacherGroups.filter((group) => group.id !== groupId);
    setTeacherGroups(updatedGroups);
  };

  const handleLogin = (e) => {
    e.preventDefault();

    if (!data.username.trim()) {
      setWhenFieldsNotInput("is-invalid");
      return;
    }

    // Add logic to handle the creation of a new group using data.username

    // Reset form fields
    setData({
      username: "",
    });

    // Close the modal
    handleClose();
  };

  const [click, setClick] = useState(false);
  const handleClick = () => setClick(!click);

  return (
    <div className='App'>
      {/* Your styles and navigation code remain unchanged */}
      
      <div className="table-responsive" style={{ fontFamily: 'Allerta Stencil' }}>
        <Table striped bordered hover responsive className="mt-4 text-center">
          <thead>
            <tr>
              <th>Group ID</th>
              <th>Group Name</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {teacherGroups.map((group) => (
              <ManagementGroups key={group.id} group={group} handleDelete={handleDelete} />
            ))}
          </tbody>
        </Table>
      </div>

      <Modal show={show} onHide={handleClose}>
        <Modal.Body>
          <div className="dashboard-content">
            <img src="https://fsx1.itstep.org/api/v1/files/8k9sZK8wYr9V3z8j9J9L7jz6Z8r4V5Qh" alt="logo" />
            <h1 className="fs-4 card-title fw-bold mb-4" style={{ textAlign: 'center' }}>Create Group</h1>
            <form onSubmit={handleLogin} className="needs-validation" noValidate autoComplete="off">
              <div className="mb-3">
                <label className="mb-2 text-muted" htmlFor="username">
                  Group Name
                </label>
                <input
                  id="username"
                  type="text"
                  className={`form-control ${whenFieldsNotInput}`}
                  name="username"
                  value={data.username}
                  onChange={(e) => setData({ ...data, username: e.target.value })}
                  required
                  autoFocus
                />
                <div className="invalid-feedback">Group Name is required</div>
              </div>

              <div className="d-flex justify-content-center">
                <button type="submit" className="btn btn-primary mx-auto">
                  Create
                </button>
              </div>
            </form>
          </div>
        </Modal.Body>
      </Modal>
    </div>
  );
}

export default ManageGroupsComponent;
