import React, { useState } from "react";
import { NavLink } from "react-router-dom";
import { Modal, Button, Container, Row, Col, Form, Table } from 'react-bootstrap';
import { HamburgetMenuClose, HamburgetMenuOpen } from "../Icons";
import ManagementGroups from "./ManagementGroups"; // Make sure to import the ManagementGroups component

const ManageGroupsComponent = () => {


  const handleClick = () => setClick(!click);
  const [click, setClick] = useState(false);
  const [show, setShow] = useState(false);
  const [groupTitle, setGroupTitle] = useState('');
  const [groupList, setGroupList] = useState([
    {
      id: 1,
      title: 'Group 1',
    },
    {
      id: 2,
      title: 'Group 2',
    },
  ]
  );




  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const handleGroupSubmit = (e) => {
    e.preventDefault();
    // Handle group submission logic here
    const newGroup = {
      id: groupList.length + 1,
      title: groupTitle,
    };
    setGroupList([...groupList, newGroup]);
    setGroupTitle(''); // Clear the groupTitle field after submitting
    handleClose(); // Close the modal after submission
  };



  return (
    <div className='App'>
   <style>
                {`
  
                  body {
                      background-color: #e3f2fd;
                      font-family: 'Allerta Stencil', sans-serif;
                      
                  }
                   .navbar {
                      background-color: #1f5156;
                      height: 100px;
                      display: flex;
                      justify-content: center;
                      align-items: center;
                      font-size: 1.2rem;
                      position: sticky;
                      top: 0;
                      z-index: 20;
                    }
                    
                    .nav-container {
                      display: flex;
                      justify-content: center;
                      align-items: center;
                      width: 100%;
                      height: 80px;
                      max-width: 1500px;
                      position: relative;
                    }
                    
                    .nav-logo {
                      color: #f5b921;
                      align-items: center;
                      margin-left: 20px;
                      cursor: pointer;
                      text-decoration: none;
                      font-size: 2rem;
                      flex-grow: 1;
                    
                      display: flex;
                      align-items: center;
                      justify-items: center;
                    }
                    
                    .nav-logo .icon {
                      display: inline-block;
                      width: 3rem;
                      height: 3rem;
                      margin-left: 16px;
                    }
                    
                    .nav-menu {
                      display: flex;
                      list-style: none;
                      text-align: center;
                      margin-right: 2rem;
                      width: 100%;
                      justify-content: space-evenly;
                       margin-top: 30px;
                    }
                    
                    .nav-links {
                      color: #fff;
                      text-decoration: none;
                      padding: 0.5rem 1rem;
                      height: 100%;
                      border-bottom: 3px solid transparent;
                    }
                    /* .fa-code {
                      margin-left: 1rem;
                    } */
                    
                    .nav-item {
                      line-height: 40px;
                      margin-right: 1rem;
                    }
                    
                    .nav-item:after {
                      content: "";
                      display: block;
                      height: 3px;
                      width: 0;
                      background: transparent;
                      transition: width 0.7s ease, background-color 0.5s ease;
